package chapter08;import java.sql.Time;import java.util.concurrent.CyclicBarrier;import java.util.concurrent.TimeUnit;public class CyclicBarrierTest {    static CyclicBarrier c = new CyclicBarrier(2);    public static void main(String[] args) throws InterruptedException {        new Thread(new Runnable() {            @Override            public void run() {                try {                    c.await();                } catch (Exception e) {                }                System.out.println(1);            }        }).start();        TimeUnit.SECONDS.sleep(2);        try {            c.await();        } catch (Exception e) {        }        System.out.println(2);    }}